frappe.ui.form.on('Pick List Item', {
	pick_batch_no: function(frm, cdt, cdn) {
		var me = locals[cdt][cdn]
		console.log(me)

		let d = new frappe.ui.Dialog({
			title: 'Enter details',
			fields: [{
					fieldname: 'item_code',
					read_only: 1,
					fieldtype: 'Link',
					options: 'Item',
					label: __('Item Code'),
					default: me.item_code
				},
				{
					fieldname: 'warehouse',
					fieldtype: 'Link',
					options: 'Warehouse',
					reqd: me.has_batch && !me.has_serial_no ? 0 : 1,
					label: __('Warehouse'),
					default: typeof me.warehouse == "string" ? me.warehouse : '',
					onchange: function(e) {
						me.warehouse = this.get_value();
						let qty_field = d.fields_dict.qty;
						let total_qty = 0;

						d.fields_dict.batches.df.data.forEach(data => {
							total_qty += flt(data.selected_qty);
						});

						qty_field.set_input(total_qty);


						var batches = this.layout.fields_dict.batches;
						if (batches) {
							batches.grid.df.data = [];
							batches.grid.refresh();
							batches.grid.add_new_row(null, null, null);
						}
					},
					get_query: function() {
						return {
							query: "erpnext.controllers.queries.warehouse_query",
							filters: [
								["Bin", "item_code", "=", me.item_code],
								["Warehouse", "is_group", "=", 0],
								["Warehouse", "company", "=", frm.doc.company]
							]
						}
					}
				},
				{
					fieldtype: 'Column Break'
				},
				{
					fieldname: 'qty',
					fieldtype: 'Float',
					read_only: me.has_batch && !me.has_serial_no,
					label: __(me.has_batch && !me.has_serial_no ? 'Selected Qty' : 'Qty'),
					default: flt(me.stock_qty) || flt(me.picked_qty),
				},
				{
					fieldname: 'uom',
					read_only: 1,
					fieldtype: 'Link',
					options: 'UOM',
					label: __('UOM'),
					default: me.uom
				},


				{
					fieldtype: 'Section Break',
					label: __('Batches')
				},
				{
					fieldname: 'batches',
					fieldtype: 'Table',
					label: __('Batch Entries'),
					fields: [{
							'fieldtype': 'Link',
							'read_only': 0,
							'fieldname': 'batch_no',
							'options': 'Batch',
							'label': __('Select Batch'),
							'in_list_view': 1,
							get_query: function() {
								return {
									filters: {
										item_code: me.item_code,
										warehouse: me.warehouse || typeof me.warehouse == "string" ? me.warehouse : ''
									},
									query: 'erpnext.controllers.queries.get_batch_no'
								};
							},
							change: function() {
								const batch_no = this.get_value();
								if (!batch_no) {
									this.grid_row.on_grid_fields_dict
										.available_qty.set_value(0);
									return;
								}
								let selected_batches = this.grid.grid_rows.map((row) => {
									if (row === this.grid_row) {
										return "";
									}

									if (row.on_grid_fields_dict.batch_no) {
										return row.on_grid_fields_dict.batch_no.get_value();
									}
								});
								if (selected_batches.includes(batch_no)) {
									this.set_value("");
									frappe.throw(__('Batch {0} already selected.', [batch_no]));
								}

								if (me.warehouse) {
									frappe.call({
										method: 'erpnext.stock.doctype.batch.batch.get_batch_qty',
										args: {
											batch_no,
											warehouse: me.warehouse,
											item_code: me.item_code
										},
										callback: (r) => {
											this.grid_row.on_grid_fields_dict
												.available_qty.set_value(r.message || 0);
										}
									});

								} else {
									this.set_value("");
									frappe.throw(__('Please select a warehouse to get available quantities'));
								}
								// e.stopImmediatePropagation();
							}
						},
						{
							'fieldtype': 'Float',
							'read_only': 1,
							'fieldname': 'available_qty',
							'label': __('Available'),
							'in_list_view': 1,
							'default': 0,
							change: function() {
								this.grid_row.on_grid_fields_dict.selected_qty.set_value('0');
							}
						},
						{
							'fieldtype': 'Float',
							'read_only': 0,
							'fieldname': 'selected_qty',
							'label': __('Qty'),
							'in_list_view': 1,
							'default': 0,
							change: function() {
								var batch_no = this.grid_row.on_grid_fields_dict.batch_no.get_value();
								var available_qty = this.grid_row.on_grid_fields_dict.available_qty.get_value();
								var selected_qty = this.grid_row.on_grid_fields_dict.selected_qty.get_value();

								if (batch_no.length === 0 && parseInt(selected_qty) !== 0) {
									frappe.throw(__("Please select a batch"));
								}
								if (parseFloat(available_qty) < parseFloat(selected_qty)) {

									this.set_value('0');
									frappe.throw(__('For transfer from source, selected quantity cannot be greater than available quantity'));
								} else {
									this.grid.refresh();
								}

								let qty_field = d.fields_dict.qty;
								let total_qty = 0;

								d.fields_dict.batches.df.data.forEach(data => {
									total_qty += flt(data.selected_qty);
								});

								qty_field.set_input(total_qty);






							}
						},
					],
					in_place_edit: true,
				}

			],
			primary_action_label: 'Insert',
			primary_action(values) {
				console.log(values);
				frappe.run_serially([
					() => {
						values.batches.map((batch, i) => {
							let batch_no = batch.batch_no;
							let row = '';


							row = frm.add_child("locations", {
							        'item_code':values.item_code,
							        'qty':batch.selected_qty,
							        'stock_qty':batch.selected_qty,
							        'batch_no':batch_no,
							        'warehouse':values.warehouse

							        })
						});
					},
					() => {

						cur_frm.refresh_field("locations");
				// 		refresh_field("packed_items");

					},
					() => {
					    d.hide()
	    				frappe.model.remove_from_locals(me.doctype,me.name)	

						cur_frm.refresh_field("locations");

					}


				])

				// d.hide();
				// cur_frm.refresh_field("locations");

			}


		});
								// 		cur_frm.refresh_field("locations");

		d.show();
				// 		cur_frm.refresh_field("locations");

	}


})

