			},
			'fieldname': [
				'percentage',
			]
		},
		async: false,
		callback: function(r) {
			if (!r.exc) {
				// code snippet
				if (r.message.percentage) {
					ItemGroup = r.message.percentage

				}
			}
		}
	});

	// getting percentage item Customer
	frappe.call({
		method: 'frappe.client.get_value',
		args: {
			'doctype': 'Customer',
			'filters': {
				'name': frm.doc.party_name
			},
			'fieldname': [
				'percentage',
			]
		},
		async: false,
		callback: function(r) {
			if (!r.exc) {
				// code snippet
				if (r.message.percentage) {
					Customer = r.message.percentage

				}
			}
		}
	});

	// getting percentage item Bom
	frappe.call({
		method: 'frappe.client.get_value',
		args: {
			'doctype': 'BOM',
			'filters': {
				'item': locals[cdt][cdn]['item_code'],
				"is_default": 1
			},
			'fieldname': [
				'raw_material_cost',
				'total_cost',
                'electricity_per_unit',
                'total_overheads',
                "quantity"
			]
		},
		async: false,
		callback: function(r) {
			if (!r.exc) {
				// code snippet
				if (r.message.raw_material_cost) {
					RMC = r.message.raw_material_cost / r.message.quantity
					TotalCost = RMC + r.message.electricity_per_unit + r.message.total_overheads

				}else 
									{
										frappe.call({
											method: 'frappe.client.get_value',
											args: {
												'doctype': 'Item',
												'filters': {
													'name': locals[cdt][cdn]['item_code']
												},
												'fieldname': [
													'trading_item_flp',
													'po_amount'
												]
											},
											async: false,
											callback: function(r) {
												if (!r.exc) {
													// code snippet
													if (r.message.trading_item_flp) {
														TotalCost = r.message.trading_item_flp
														RMC = r.message.po_amount

													}
												}
											}
										});


									}

			}
		}
	});



	if (isNaN(ItemGroup) == false && isNaN(Item) == false && isNaN(Customer) == false && isNaN(TotalCost) == false && isNaN(RMC) == false) {
		Totalper = ItemGroup + Item + Customer

		TotalValue = (Totalper * TotalCost) / 100

		locals[cdt][cdn]['rmc_'] = RMC.toFixed(2);
		locals[cdt][cdn]['flp_'] = TotalCost.toFixed(2);
		locals[cdt][cdn]['ufp_'] = (TotalCost + TotalValue).toFixed(2);



	} else {


	}




