html:
{% set dob = frappe.db.get_value("Patient", doc.patient, "dob") %}
{% set years = 0 %}
{% set months = 0 %}
{% set days = 0 %}
{% if dob %}
    {% set now  = frappe.utils.nowdate() %}
    {% set diff = frappe.utils.date_diff(now, dob) %}
    {% set years = diff//365 %}
    {% set months = (diff - (years * 365))//30 %}
    {% set days = ( (diff - (years * 365)) - (months * 30) ) %}
{% endif %}
{% set age_display = "" %}
{% if years > 0 %}
    {% set age_display = years|str + 'Y' %}
{% else %}
    {% set age_display = months|str + 'M ' + days|str + 'D'  %}
{% endif %}
<div style="font-family: arial, sans-serif;">
    <b><div style="text-align: center; margin-bottom: -2px; font-size:8px;">{{doc.patient_name|upper}}<br>
    {{doc.patient}}, {{age_display}} / {{doc.patient_gender[:1]}}</div>
    <div style="text-align:center;"><img class="barcode" style="width: 350px; height: 50px;" src="data:image/png;base64,{{ generate_barcodes(doc.name) }}"></div>
    <div  style="text-align:center; font-size:8px; margin-top: -8px;">{{doc.name}}</div>
    <div style="text-align: center; font-size:8px;">{{doc.specimen_type|upper}}</div></b>
</div>

